jQuery(document).ready(function($){$("div[id^=qafp-faq]").each(function(){var num=this.id.match(/qafp-faq(\d+)/)[1];var faqContainer=$(".qafp-faqs");var faq=$("#qafp-faq"+num);if(faqContainer.is(".collapsible")){faq.find(".qafp-faq-anchor").bind("click",function(){if(faqContainer.is(".accordion"))$(".qafp-faq-answer").not("#qafp-faq"+num+" .qafp-faq-answer").hide();if(faqContainer.is(".animation-fade"))faq.find(".qafp-faq-answer").fadeToggle();else if(faqContainer.is(".animation-slide"))faq.find(".qafp-faq-answer").slideToggle();
else faq.find(".qafp-faq-answer").toggle();if(faqContainer.is(".focus")){theOffset=$(this).offset();$(window).scrollTop(theOffset.top-50)}return false});$(".expand-all.expand").bind("click",function(){$(".expand-all.expand").hide();$(".expand-all.collapse").show();if(faqContainer.is(".animation-fade"))$(".qafp-faq-answer").fadeIn(400);else if(faqContainer.is(".animation-slide"))$(".qafp-faq-answer").slideDown();else $(".qafp-faq-answer").show()});$(".expand-all.collapse").bind("click",function(){$(".expand-all.collapse").hide();
$(".expand-all.expand").show();if(faqContainer.is(".animation-fade"))$(".qafp-faq-answer").fadeOut(400);else if(faqContainer.is(".animation-slide"))$(".qafp-faq-answer").slideUp();else $(".qafp-faq-answer").hide()})}});$(".qasubmission").bind("click",function(){$("#postbox").fadeToggle()});$("#qafp_searchform").submit(function(){link=$(this).find("#qafp_search_link").val();query=$(this).find(".qafp_search").val();query=query.split(" ").join("+");link=link+query;location.href=link;return false})});